INSERT INTO web_komentare VALUES (601, 155, 1151915777, 'Wara', '', '', 'MS Deutshland', 'Hmm, Prtugalci nejsou špatný, ale hrajou hrozně pr.. fotbal- samý provokace, pošťuchování, tahání za dres, simulování... To jest přesně něco co na hřišti nemám rád...\r\nOno z tý poslední čtyřky (ITA:GER, FRA:POR) si člověk těžko vybere, ale Argentina si to zkazila sama naprosto prokaučovaným čtvrtfinále, navíc nehrála nijak oslnivej fotbal, kromě zápasu s totálně vyautovaným Srbskem. To samý Brazílie, tyhle dva týmy byly aspoň pro mě fakt velkým zklamáním, jako ostatně skoro celý mistrovství- všichni brání, a africké týmy,  který jediný hráli nahoru dolů (Pobřeží Slonoviny, Ghana) už jsou venku. Anglii mám rád, výborní fotbalisté, ale jejich fotbal stál taky zaprd a penalty, to opravdu není jejich kafe.\r\nTakže to asi budu přát Francii, kvůli Zidanovi a Henrymu, protože to jsou oba dva super fotbalisti...\r\nBTW: Kdybych musel volit tým podle \"krásy\" předváděnýho fotbalu, byli by to asi Němci, protože ty jsou doma fakt celkem dobrý, a aspoň se snaží hrát útočnej fotbal- kralujou ostatně i statistikám co se týká střel na bránu apod.. Jenže Němce já prostě nemám rád ;-).', '\n	<p>Hmm, Prtugalci nejsou špatný, ale hrajou hrozně pr.. fotbal- samý provokace, pošťuchování, tahání za dres, simulování&#8230; To jest přesně něco co na hřišti nemám rád&#8230; Ono z&#160;tý poslední čtyřky (ITA:GER, FRA:POR) si člověk těžko vybere, ale Argentina si to zkazila sama naprosto prokaučovaným čtvrtfinále, navíc nehrála nijak oslnivej fotbal, kromě zápasu s&#160;totálně vyautovaným Srbskem. To samý Brazílie, tyhle dva týmy byly aspoň pro mě fakt velkým zklamáním, jako ostatně skoro celý mistrovství- všichni brání, a africké týmy, který jediný hráli nahoru dolů (Pobřeží Slonoviny, Ghana) už jsou venku. Anglii mám rád, výborní fotbalisté, ale jejich fotbal stál taky zaprd a penalty, to opravdu není jejich kafe. Takže to asi budu přát Francii, kvůli Zidanovi a Henrymu, protože to jsou oba dva super fotbalisti&#8230; BTW: Kdybych musel volit tým podle &#8222;krásy&#8220; předváděnýho fotbalu, byli by to asi Němci, protože ty jsou doma fakt celkem dobrý, a aspoň se snaží hrát útočnej fotbal- kralujou ostatně i&#160;statistikám co se týká střel na bránu apod.. Jenže Němce já prostě nemám rád <img class=\"smajl\" width=\"15\" height=\"15\" src=\"./i/smajly/wink.gif\" alt=\";-)\" />.</p>\n\n<!-- generated by Texy! -->', '213.250.225.118', '1');
