INSERT INTO web_komentare VALUES (623, 157, 1152223920, 'Wara', '', '', '', 'ad cyklistické helmy: nevím co se čílíte, povinnost mít ji na hlavě platila už před touhle novelou, jenom se zvedla věková hranice do kdy je helma povinná z 15let na 18let...\r\n\r\nbody za to, že nemáte na hlavě helmu, nedostanete žádný, cyklista ani chodec nemůže žádné body dostat, i když je vlastníkem řidičského průkazu... hrozí tak \"pouze\" finanční pokuta na místě do výše maximálně 2000kč a ve správním řízení pak v rozmezí 1500-2500kč\r\n\r\nzajímavost pro \"huliče\": testy a tresty se netýkají jenom alkoholu, pokud vám v krvi najdou stopové zbytky třebas trávy, je to za 7 bodů, pokutu až 50 000kč, a zároveň se to dá posuzovat jako trestný čin za který hrozí až 3 roky vězení- vtip je v tom, že stopy marihuany vám v krvi klidně mohou vydržet i měsíc od její poslední konzumace...', '\n	<p>ad cyklistické helmy: nevím co se čílíte, povinnost mít ji na hlavě platila už před touhle novelou, jenom se zvedla věková hranice do kdy je helma povinná z&#160;15let na 18let&#8230;</p>\n\n	<p>body za to, že nemáte na hlavě helmu, nedostanete žádný, cyklista ani chodec nemůže žádné body dostat, i&#160;když je vlastníkem řidičského průkazu&#8230; hrozí tak &#8222;pouze&#8220; finanční pokuta na místě do výše maximálně 2000kč a ve správním řízení pak v&#160;rozmezí 1500&#8211;2500kč</p>\n\n	<p>zajímavost pro &#8222;huliče&#8220;: testy a tresty se netýkají jenom alkoholu, pokud vám v&#160;krvi najdou stopové zbytky třebas trávy, je to za 7&#160;bodů, pokutu až 50&#160;000kč, a zároveň se to dá posuzovat jako trestný čin za který hrozí až 3&#160;roky vězení- vtip je v&#160;tom, že stopy marihuany vám v&#160;krvi klidně mohou vydržet i&#160;měsíc od její poslední konzumace&#8230;</p>\n\n<!-- generated by Texy! -->', '213.250.225.118', '1');
