INSERT INTO web_komentare VALUES (1356, 287, 1183926122, 'bouchy', '', 'http://boss-john.net', '', 'Zdravím, \r\n\r\ntak s tímhle foťákem jsem prožil hrozně moc :). Několikrát jsem ho měl na horách, kde jsem ho vyválel ve sněhu, několikrát jsem s ním sjížděl Vltavu - to jsem ho zase namočil. Už ani nevím, kolikrát mi spadl na zem, ale jedno vím jistě :). Mám ho už pátým rokem a pořád se drží a fotí dobře, to je důležité. Sice už pokukuji po nějakém nástupci, ale tenhle stroj si nechám doma jako symbol odolnosti :).', '\n	<p>Zdravím,</p>\n\n	<p>tak s&#160;tímhle foťákem jsem prožil hrozně moc <img class=\"smajl\" width=\"15\" height=\"15\" src=\"./i/smajly/smile.gif\" alt=\":)\" />. Několikrát jsem ho měl na horách, kde jsem ho vyválel ve sněhu, několikrát jsem s&#160;ním sjížděl Vltavu &#8211; to jsem ho zase namočil. Už ani nevím, kolikrát mi spadl na zem, ale jedno vím jistě <img class=\"smajl\" width=\"15\" height=\"15\" src=\"./i/smajly/smile.gif\" alt=\":)\" />. Mám ho už pátým rokem a pořád se drží a fotí dobře, to je důležité. Sice už pokukuji po nějakém nástupci, ale tenhle stroj si nechám doma jako symbol odolnosti <img class=\"smajl\" width=\"15\" height=\"15\" src=\"./i/smajly/smile.gif\" alt=\":)\" />.</p>\n\n<!-- generated by Texy! -->', '80.188.217.242', '1');
