INSERT INTO web_komentare VALUES (1568, 363, 1215074733, 'glumet', '', 'http://boss-john.net', '', 'Taky jsem teď tři týdny makal v betonárně (měsíc pauza a v srpnu jedu zase :) ). Odpoledne to sice šlo - pivko, kolo apod., ale večer už jsem byl z celý směny tak stahanej, že jsem usínal v deset večer :). ', '\n	<p>Taky jsem teď tři týdny makal v betonárně (měsíc pauza a v srpnu jedu zase <img src=\"./i/smajly/smile.gif\" alt=\":)\" class=\"smajl\" /> ). Odpoledne to sice šlo – pivko, kolo apod., ale večer už jsem byl z celý směny tak stahanej, že jsem usínal v deset večer <img src=\"./i/smajly/smile.gif\" alt=\":)\" class=\"smajl\" />.</p>\n\n<!-- by Texy2! -->', '80.188.217.242', '1');
INSERT INTO web_komentare VALUES (1569, 364, 1215166604, 'glumet', '', 'http://boss-john.net', '', 'V GPS technologiích mám hokej, takže moc neporadím, ale kdybych si měl osobně pořídit jeden z těch dvou, bral bych ten levnější. Stejně bych ho používal jen k hledání kešek (zatím jsem našel 4 bez GPS, ale není to ono... zabil jsem tím strašnýho času :) ). Koukal jsem se na ten spodní odkaz a jak někde vidím \"sympaticky malé rozměry a hmotnost\", dávám takovému produktu malé významné plus :). Ale v nejbližší době u mě nákup modulu stejně nehrozí, teď je pro mě prioritou noťas a po třech měsících vybírání jsem snad konečně našel to pravé. Můžeš namrknout :) - http://tinyurl.com/6edf99', '\n	<p>V GPS technologiích mám hokej, takže moc neporadím, ale kdybych si měl osobně pořídit jeden z těch dvou, bral bych ten levnější. Stejně bych ho používal jen k hledání kešek (zatím jsem našel 4 bez GPS, ale není to ono… zabil jsem tím strašnýho času <img src=\"./i/smajly/smile.gif\" alt=\":)\" class=\"smajl\" /> ). Koukal jsem se na ten spodní odkaz a jak někde vidím „sympaticky malé rozměry a hmotnost“, dávám takovému produktu malé významné plus <img src=\"./i/smajly/smile.gif\" alt=\":)\" class=\"smajl\" />. Ale v nejbližší době u mě nákup modulu stejně nehrozí, teď je pro mě prioritou noťas a po třech měsících vybírání jsem snad konečně našel to pravé. Můžeš namrknout <img src=\"./i/smajly/smile.gif\" alt=\":)\" class=\"smajl\" /> – <a href=\"http://tinyurl.com/6edf99\">http://tinyurl.com/6edf99</a></p>\n\n<!-- by Texy2! -->', '80.188.217.242', '1');
INSERT INTO web_komentare VALUES (1570, 364, 1215175752, 'Zgrot', 'krisikj@centrum.cz', '', '', 'Cus, mam Evolve goTraxx. Na Geocaching staci, na nic jinyho ho nepouzivam. Nevim jaky mas telefon, ale doporucil by neco s operacnim systemem. Ja mam jenom SE W810i a Handycaching... Takze chodim jenom za šipkou bez mapy. Vyhodou je, ze se chytnu skoro vsude, ale brzdi me telefon, casto hlasi chybu, ale to neni podle me chyba modulu. Drazsi bych nekupoval, nejsou o moc lepsi, tam platis spis za vzhled a znacku. Diody a vzhled neresim, stejne ho mam v kapse, kde neni videt. Jestli te hledani pokladu semele, tak budes casem stejne premyslet o turisticke navigaci.\r\n', '\n	<p>Cus, mam Evolve goTraxx. Na Geocaching staci, na nic jinyho ho nepouzivam. Nevim jaky mas telefon, ale doporucil by neco s operacnim systemem. Ja mam jenom SE W810i a Handycaching… Takze chodim jenom za šipkou bez mapy. Vyhodou je, ze se chytnu skoro vsude, ale brzdi me telefon, casto hlasi chybu, ale to neni podle me chyba modulu. Drazsi bych nekupoval, nejsou o moc lepsi, tam platis spis za vzhled a znacku. Diody a vzhled neresim, stejne ho mam v kapse, kde neni videt. Jestli te hledani pokladu semele, tak budes casem stejne premyslet o turisticke navigaci.</p>\n\n<!-- by Texy2! -->', '88.103.160.55', '1');
