INSERT INTO web_komentare VALUES (1578, 368, 1221514263, 'LaserD@nce', '', 'http://www.laserdance.xf.cz', '', 'Geocaching je paráda, to máš pravdu.. Objevil jsem ho asi před rokem, a vyrážím do terénu s Garmin eTrex Legend.. Míval jsem P910i s GPS modulem podobným tomu tvému, ale vadila mi jakási nehorabanost a malá výdrž baterií.. Takže doporučuji koupi outdoor GPS, mám ji i místo tachometru na kole a jako doplňek na mašinu :).', '\n	<p>Geocaching je paráda, to máš pravdu.. Objevil jsem ho asi před rokem, a vyrážím do terénu s Garmin eTrex Legend.. Míval jsem P910i s GPS modulem podobným tomu tvému, ale vadila mi jakási nehorabanost a malá výdrž baterií.. Takže doporučuji koupi outdoor GPS, mám ji i místo tachometru na kole a jako doplňek na mašinu <img src=\"./i/smajly/smile.gif\" alt=\":)\" class=\"smajl\" />.</p>\n\n<!-- by Texy2! -->', '213.192.1.30', '1');
