INSERT INTO web_komentare VALUES (1594, 374, 1230828921, 'p', '', '', '', 'Preciznost provedeni?? Ted jsme se tady u pocitaci zasmali jako uz dlouho ne.Ty jsi uvnitr bud vubec nebyl nebo nosis bilou hul.Vsude rozbita dlazba, couhajici kabely z der na zemi, kybly mezi regalama, protoze do obchodu tece (kvuli tomu nekolik obchodu zavrenych, protoze menili podlahy), rozbite vytahy a poruchy eskalatoru, chybejici obklady, ktere byly v projektu, venku totalni staveniste, o vzhledu budovy ani nemluvit, opravdu fantasticka architektura do centra krajskeho mesta. Asi proto se tak rychle chytla prezdivka vezeni:-)) Mel bys skocit k ocnimu ', '\n	<p>Preciznost provedeni?? Ted jsme se tady u pocitaci zasmali jako uz dlouho ne.Ty jsi uvnitr bud vubec nebyl nebo nosis bilou hul.Vsude rozbita dlazba, couhajici kabely z der na zemi, kybly mezi regalama, protoze do obchodu tece (kvuli tomu nekolik obchodu zavrenych, protoze menili podlahy), rozbite vytahy a poruchy eskalatoru, chybejici obklady, ktere byly v projektu, venku totalni staveniste, o vzhledu budovy ani nemluvit, opravdu fantasticka architektura do centra krajskeho mesta. Asi proto se tak rychle chytla prezdivka vezeni:-)) Mel bys skocit k ocnimu</p>\n\n<!-- by Texy2! -->', '77.236.208.14', '1');
