INSERT INTO web_texty VALUES (38, 1, 1130058343, '1', 'Bude se slavit!! (snad)', 'Tak už za 2 hoďky odjíždíme k poslednímu podzimnímu zápasu do Herálce. Budem potřebovat štěstí, protože to určitě lehkej zápas nebude. Ale když vyhrajem, tak si upevníme první místo a to by bylo úplně skvělý. Takže musíme makat a jezdit po zadku :-)) Až přijedem, asi tak okolo páté hodiny, tak nás čeká druhý svátek dnešního dne. Derby Slavia - Sparta.', 'Bylo by to skvělý, kdyby Sparta vyhrála a Slavii v tabulce přeskočila. Budeme s kámošema určitě fandit, to bude zase atmosféra v hospě, půlka Sparťani, půlka Slávisti. Na to se moc těšim. Horší to bude zítra se školou, 9 hodin, a skoro každou hodinu nějaká písemka. Nějek mě to tam neláká...a jestli se podaří double výhra (Sparta, a my ;-)))) ), tak se tam asi těžko objevim. Lepší 9 zameškaných hodin než pár kulí z Chemie, Bižule apod. A dneska fakt nemám náladu se učit. ', '\n	<p>Tak už za 2&#160;hoďky odjíždíme k&#160;poslednímu podzimnímu zápasu do Herálce. Budem potřebovat štěstí, protože to určitě lehkej zápas nebude. Ale když vyhrajem, tak si upevníme první místo a to by bylo úplně skvělý. Takže musíme makat a jezdit po zadku <img class=\"smajl\" width=\"15\" height=\"15\" src=\"./i/smajly/smile.gif\" alt=\":-))\" /> Až přijedem, asi tak okolo páté hodiny, tak nás čeká druhý svátek dnešního dne. Derby Slavia &#8211; Sparta.</p>\n\n<!-- generated by Texy! -->', '\n	<p>Bylo by to skvělý, kdyby Sparta vyhrála a Slavii v&#160;tabulce přeskočila. Budeme s&#160;kámošema určitě fandit, to bude zase atmosféra v&#160;hospě, půlka Sparťani, půlka Slávisti. Na to se moc těšim. Horší to bude zítra se školou, 9&#160;hodin, a skoro každou hodinu nějaká písemka. Nějek mě to tam neláká&#8230;a jestli se podaří double výhra (Sparta, a my <img class=\"smajl\" width=\"15\" height=\"15\" src=\"./i/smajly/wink.gif\" alt=\";-))))\" /> ), tak se tam asi těžko objevim. Lepší 9&#160;zameškaných hodin než pár kulí z&#160;Chemie, Bižule apod. A&#160;dneska fakt nemám náladu se učit.</p>\n', '1', '0');
