INSERT INTO web_texty VALUES (126, 1, 1145269853, '1', 'Vepřová - Sobíňov 0:2 (0:0)', 'Včera jsme odehráli první jarní utkání ve Vepřové. Byl to souboj prvního a druhého. Před zápasem jsme měli na Vepřovou náskok dvou bodů, takže jsme potřebovali vyhrát. Už v sobotu hrála Nová Ves ve třetím Herálci, který byl za námi také jen o dva body a bohužel pro nás zvítězil. Takže se dočasně vyhoupnul na první místo a nám tedy opravdu nezbývalo nic jiného než výhra.\r\n Zápas jsme začali menšim počátečnim náporem, který potom bohužel ustal. Celý první poločas jsme si nevypracovali žádnou vážnější šanci, i když jsme měli velikou územní převahu. Po poločase tedy 0:0.\r\n Druhý poločas jsme hráli na nerovném povrchu do kopce a ještě foukal docela silný protivítr, nic příjemného. Ale první gól se povedlo dát J. Ondráčkovi přímo z rohu, takže přišlo menší uklidnění a hlavně obrovská radost. Pak jsme mohli ještě přidat kolikrát pojistku v podobě druhého gólu do sítě, ale povedlo se to až znovu J. Ondráčkovi. V závěru vstřelil ještě třetí gól, ale ten nebyl uznán pro **ofsajd**. Jestli byl, o tom by se dalo polemizovat.\r\n Zápas tedy skončil 2:0 pro nás, fanoušků opět přijelo hafo moc, podpora velká. Večer jsme to pořádně oslavili :-))', '', '\n	<p>Včera jsme odehráli první jarní utkání ve Vepřové. Byl to souboj prvního a druhého. Před zápasem jsme měli na Vepřovou náskok dvou bodů, takže jsme potřebovali vyhrát. Už v&#160;sobotu hrála Nová Ves ve třetím Herálci, který byl za námi také jen o&#160;dva body a bohužel pro nás zvítězil. Takže se dočasně vyhoupnul na první místo a nám tedy opravdu nezbývalo nic jiného než výhra.\n	<br />Zápas jsme začali menšim počátečnim náporem, který potom bohužel ustal. Celý první poločas jsme si nevypracovali žádnou vážnější šanci, i&#160;když jsme měli velikou územní převahu. Po poločase tedy 0:0.\n	<br />Druhý poločas jsme hráli na nerovném povrchu do kopce a ještě foukal docela silný protivítr, nic příjemného. Ale první gól se povedlo dát J. Ondráčkovi přímo z&#160;rohu, takže přišlo menší uklidnění a hlavně obrovská radost. Pak jsme mohli ještě přidat kolikrát pojistku v&#160;podobě druhého gólu do sítě, ale povedlo se to až znovu J. Ondráčkovi. V&#160;závěru vstřelil ještě třetí gól, ale ten nebyl uznán pro <strong>ofsajd</strong>. Jestli byl, o&#160;tom by se dalo polemizovat.\n	<br />Zápas tedy skončil 2:0 pro nás, fanoušků opět přijelo hafo moc, podpora velká. Večer jsme to pořádně oslavili :-))</p>\n\n<!-- generated by Texy! -->', '', '1', '0');
